package com.syntax.class01;

public class WhileLoopEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=50;
		while(num>=20) {
			System.out.print(num+" ");
			num--;
			
		}
		System.out.println("----------");
		int i=2;
		while(i<20) {
			System.out.print(i);
			i+=2;
			
			
		}

	}

}
